mapper.select:
   
  select * from (select rownum rnum,a.* from (
  
select member_id as id, member_idx as idx
from member 
where member_idx in (
select useridx from blackList 
)
 
    ) a)b where rnum>=#{startNum} and rnum<=#{endNum}

   
  select * from (select rownum rnum,a.* from (
  
select member_id as id, member_idx as idx
from member 
where member_idx in (
select useridx 
from blackList 
)
and member_id = #{id}
 
    ) a)b where rnum>=#{startNum} and rnum<=#{endNum}

		select count(*) from
		blackList
		where useridx = (
		select member_idx from member
		where member_id = #{id}
		)
